import './City.css'
import React, {useState} from 'react';
import Search from './Search';
import axios from 'axios';
import FormattedDate from './FormattedDate';

function City(props) {

    const [city, setCity] = useState(props.defaultCity);

    function handleCityChange(event){
        setCity(event.target.value)
    }

    let weatherData = {
        city: "Madrid",
        date: "Sunday, January 01, 2023",
        updateTime: "Last time updated: 00:00",
        weatherDescription: "Clear Sky",
    }
    return (

        <div>
            <h1 className="City">{props.data.city}</h1>
            <FormattedDate className="currentDate">{props.data.date}</FormattedDate>
            <p className='currentTime'>{weatherData.updateTime}</p>
            <p className="currentWeather">{props.data.description}</p>
        </div>
    );
}

export default City;
