import React, { useState } from "react";

export default function WeatherTemperature(props) {
  const [unit, setUnit] = useState("celsius");
  function showFahrenheit(event) {
    event.preventDefault();
    setUnit("fahrenheit");
  }

  function showCelsius(event) {
    event.preventDefault();
    setUnit("celsius");
  }

  function fahrenheitConversion() {
    return (props.celsius * 9) / 5 + 32;
  }

  if (unit === "celsius") {
  return (
    <div className="WeatherTemperature">
      <span className="temperature">{Math.round(props.celsius)}</span>
      <span className="unit">째C | <a href="/" onClick={showFahrenheit}>째F</a>
      </span>
    </div>
  );
} else {
  return (
    <div className="WeatherTemperature">
      <span className="temperature">{Math.round(fahrenheitConversion())}</span>
      <span className="unit"><a href="/" onClick={showCelsius}>째C</a> | 째F
      </span>
    </div>
  );

}
}
