import './City.css'
import React from 'react';
import axios from 'axios';

function City() {

    function handleResponse(response){
        console.log(response.data);
    }
    let weatherData = {
        city: "Madrid",
        date: "Sunday, January 01, 2023",
        updateTime: "Last time updated: 00:00",
        weatherDescription: "Clear Sky",
    }
    const apiKey = "9f9579090daa27d42e77fa3c3fec2fd7";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${weatherData.city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then.apply(handleResponse);

    return (
        <div>
            <h1 className="City">{weatherData.city}</h1>
            <p className="currentDate">{weatherData.date}</p>
            <p className='currentTime'>{weatherData.updateTime}</p>
            <p className="currentWeather">{weatherData.weatherDescription}</p>
        </div>
    );
}

export default City;
